export const arweave = Arweave.init({});

export const createPostInfo = async (node) => {
  const ownerAddress = node.owner.address;
  const height = node.block ? node.block.height : -1;
  const timestamp = node.block ? parseInt(node.block.timestamp, 10) * 1000 : -1;
  const postInfo = {
    txid: node.id,
    owner: ownerAddress,
    height: height,
    length: node.data.size,
    timestamp: timestamp,
  }
  
  postInfo.request = await arweave.api.get(`/${node.id}`, { timeout: 10000 })
  postInfo.emoji = getRandomEmoji()
  return postInfo;
 }

let tags = [
  {
    name: "App-Name",
    values: ["Web3ChatterTest1"]
  },
  {
    name: "Content-Type",
    values: ["text/plain"]
  }
]

export const buildQuery = (topic) => {
  let stringifiedTags = [...tags]
  if (topic) {
    console.log('topic: ', topic)
    stringifiedTags = [...tags, {
      name: "Topic",
      values: [topic]
    }]
  }
  stringifiedTags = JSON.stringify(stringifiedTags).replace(/"([^"]+)":/g, '$1:')
  const queryObject = { query: `{
    transactions(first: 50,
      tags: ${stringifiedTags}
    ) {
      edges {
        node {
          id
          owner {
            address
          }
          data {
            size
          }
          block {
            height
            timestamp
          }
          tags {
            name,
            value
          }
        }
      }
    }
  }`}
  console.log('queryObject: ', queryObject)
  return queryObject;
}

